	TGFPRO.CODGRUPOPROD, TGFGRU.DESCRGRUPOPROD, TGFTOP.GOLSINAL , SUM( CASE WHEN TGFTOP.GOLSINAL = 1 THEN 0 ELSE ( ((ITE.VLRTOT - ITE.VLRDESC - NVL(ITE.VLRREPRED,0) + ITE.VLRSUBST + ITE.VLRIPI)  * VCA.INDITENSBRUTO) * CASE WHEN TGFTOP.BONIFICACAO = 'S' THEN 0 ELSE 1 END * TGFTOP.GOLDEV ) END ) AS VALORVENDA, SUM( CASE WHEN TGFTOP.GOLSINAL = 1 THEN 0 ELSE (((((ITE.VLRTOT - ITE.VLRDESC - NVL(ITE.VLRREPRED,0) + ITE.VLRSUBST + ITE.VLRIPI)  * VCA.INDITENSBRUTO) * CASE WHEN TGFTOP.BONIFICACAO = 'S' THEN 0 ELSE 1 END) - (( ((( (ITE.VLRTOT - ITE.VLRDESC - NVL(ITE.VLRREPRED,0)) * VCA.INDITENSBRUTO * ((CASE WHEN CGM.TEMPERCPARTSIMP = 'S' THEN (CASE WHEN TGFTOP.TEMPIS = 'S' THEN NVL(CGM.PERCPIS,0) ELSE 0 END) ELSE (CASE WHEN NVL(CGM.PERCPIS, 0) = 0 THEN 0 WHEN TGFTOP.TEMPIS = 'S' AND NOT  EXISTS (SELECT(1) FROM TGFDIN DIN WHERE DIN.CODIMP = 6 AND DIN.NUNOTA = ITE.NUNOTA AND DIN.SEQUENCIA = ITE.SEQUENCIA) THEN NVL(CGM.PERCPIS, 0) ELSE 0 END) END) + (CASE WHEN CGM.TEMPERCPARTSIMP = 'S' THEN (CASE WHEN TGFTOP.TEMCSL = 'S' THEN NVL(CGM.PERCCSL,0) ELSE 0 END) ELSE (CASE WHEN NVL(CGM.PERCCSL, 0) = 0 THEN 0 WHEN TGFTOP.TEMCSL = 'S' AND NOT  EXISTS (SELECT(1) FROM TGFDIN DIN WHERE DIN.CODIMP = 9 AND DIN.NUNOTA = ITE.NUNOTA AND DIN.SEQUENCIA = ITE.SEQUENCIA) THEN NVL(CGM.PERCCSL, 0) ELSE 0 END) END) + (CASE WHEN CGM.TEMPERCPARTSIMP = 'S' THEN (CASE WHEN TGFTOP.TEMCOFINS = 'S' THEN NVL(CGM.PERCCOFINS,0) ELSE 0 END) ELSE (CASE WHEN NVL(CGM.PERCCOFINS, 0) = 0 THEN 0 WHEN TGFTOP.TEMCOFINS = 'S' AND NOT  EXISTS (SELECT(1) FROM TGFDIN DIN WHERE DIN.CODIMP = 7 AND DIN.NUNOTA = ITE.NUNOTA AND DIN.SEQUENCIA = ITE.SEQUENCIA) THEN NVL(CGM.PERCCOFINS, 0) ELSE 0 END) END) + (CASE WHEN CGM.TEMPERCPARTSIMP = 'S' THEN (CASE WHEN TGFTOP.TEMPIS = 'S' THEN NVL(CGM.PERCIRPJ,0) ELSE 0 END) ELSE (CASE WHEN NVL(CGM.PERCIRPJ, 0) = 0 THEN 0 WHEN TGFTOP.TEMPIS = 'S' AND NOT  EXISTS (SELECT(1) FROM TGFDIN DIN WHERE DIN.CODIMP = 10 AND DIN.NUNOTA = ITE.NUNOTA AND DIN.SEQUENCIA = ITE.SEQUENCIA) THEN NVL(CGM.PERCIRPJ, 0) ELSE 0 END) END) + (CASE WHEN CGM.TEMPERCPARTSIMP = 'S' THEN (CASE WHEN TGFTOP.TEMPIS = 'S' THEN NVL(CGM.PERCCPP,0) ELSE 0 END) ELSE (CASE WHEN NVL(CGM.PERCCPP, 0) = 0 THEN 0 WHEN TGFTOP.TEMPIS = 'S' AND NOT  EXISTS (SELECT(1) FROM TGFDIN DIN WHERE DIN.CODIMP = 11 AND DIN.NUNOTA = ITE.NUNOTA AND DIN.SEQUENCIA = ITE.SEQUENCIA) THEN NVL(CGM.PERCCPP, 0) ELSE 0 END) END) + CASE WHEN CGM.TEMPERCPARTSIMP = 'S' THEN NVL(SNK_GetSomaPartilha(ITE.NUNOTA, ITE.SEQUENCIA), 0 ) ELSE 0 END)) + ((ITE.VLRTOT - ITE.VLRDESC - NVL(ITE.VLRREPRED,0) + ITE.VLRSUBST + ITE.VLRIPI) * VCA.INDITENSBRUTO * (NVL(CGM.PERCCUSVAR,0) + NVL(CGM.PERCMARGEM,0) + NVL(0,0) + NVL(0,0) + NVL(0,0) + NVL(0,0))) + ((ITE.VLRTOT - ITE.VLRDESC - NVL(ITE.VLRREPRED,0) + ITE.VLRIPI) * VCA.INDITENSBRUTO * NVL(CGM.PERCCVARSEMST,0))) / 100.0000) + ITE.VLRSUBST + ITE.VLRIPI + (CASE WHEN CGM.TEMPERCPARTSIMP = 'S' THEN (0) ELSE ((NVL((SELECT SUM(CASE WHEN DIN.CODIMP = 1 THEN NVL(DIN.VLRDIFALDEST,0) + NVL(DIN.VLRDIFALREM,0) + NVL(DIN.VLRFCP,0) + NVL(DIN.VLRFCPINT,0) ELSE NVL(DIN.VALOR,0) END) FROM TGFDIN DIN WHERE DIN.NUNOTA = ITE.NUNOTA AND DIN.SEQUENCIA = ITE.SEQUENCIA AND DIN.CODIMP IN (1, 4, 5, 6, 7, 8, 9, 10, 11)), 0)) ) END) ) + (CASE WHEN EMP.ICMSGERENCIA = 'N' OR (EMP.ICMSGERENCIA = 'P' AND TGFPRO.ICMSGERENCIA = 'N') THEN 0 WHEN 0 > 0 THEN 0 WHEN TGFCAB.VLRICMS = 0 THEN 0 ELSE ( (ITE.VLRICMS - NVL(ITE.VLRREPRED,0)) * TGFCAB.VLRICMS / NVL((SELECT CASE WHEN SUM(ITE.VLRICMS - NVL(ITE.VLRREPRED,0)) = 0  THEN TGFCAB.VLRICMS ELSE SUM(ITE.VLRICMS - NVL(ITE.VLRREPRED,0)) END FROM TGFITE WHERE NUNOTA = TGFCAB.NUNOTA AND SEQUENCIA > 0), TGFCAB.VLRICMS) ) END) - 0 + (((ITE.VLRTOT - ITE.VLRDESC - NVL(ITE.VLRREPRED,0) + ITE.VLRSUBST + ITE.VLRIPI) * (CASE WHEN TIPIPIEMB = 'I' THEN VLREMB + IPIEMB + TGFCAB.VLRICMSEMB ELSE 0 END + VLRDESTAQUE + CASE WHEN TIPFRETE =  'S' OR CIF_FOB IN ('C', 'R') THEN VLRFRETE ELSE 0 END)/ CASE WHEN VCA.VLRITENS = 0 THEN 1 ELSE VCA.VLRITENS END) +  ((ITE.VLRTOT - ITE.VLRDESC - NVL(ITE.VLRREPRED,0) + ITE.VLRSUBST + ITE.VLRIPI) * (CASE WHEN VCA.VLRITENS <> 0 AND TIPFRETE =  'N' AND CIF_FOB IN ('C', 'R') THEN ICMSFRETE * -1 ELSE 0 END)  / CASE WHEN VCA.VLRITENS = 0 THEN 1 ELSE VCA.VLRITENS END  * CASE WHEN (VLRNOTA - VLRJURO + VLRDESCTOT - VLRDESTAQUE) = 0 THEN 1 ELSE (VLRNOTA / (VLRNOTA - VLRJURO + VLRDESCTOT - VLRDESTAQUE)) END ) )  +  NVL(((SELECT MAX(CUS.CUSREP)    FROM TGFCUS CUS WHERE CUS.CODPROD = ITE.CODPROD    AND CUS.DTATUAL = (SELECT MAX (CN.DTATUAL) 	FROM TGFCUS CN 	WHERE CN.CODPROD = CUS.CODPROD 	AND CN.DTATUAL <= TGFCAB.DTNEG 	 	AND CN.CODLOCAl = CUS.CODLOCAL 	 ))  ), (ITE.VLRTOT - ITE.VLRDESC + ITE.VLRSUBST + ITE.VLRIPI) * VCA.INDITENSBRUTO / (CASE WHEN ITE.QTDNEG=0 THEN 1 ELSE ITE.QTDNEG END) ) * ITE.QTDNEG + 0) - (((ITE.VLRTOT - ITE.VLRDESC - NVL(ITE.VLRREPRED,0) + ITE.VLRSUBST + ITE.VLRIPI)  * VCA.INDITENSBRUTO) *  (0.0 + (NVL(CASE WHEN ITE.USOPROD = 'S' THEN CGM.PERCCFSERVICO WHEN ITE.USOPROD = 'V' THEN CGM.PERCCFFAB ELSE CGM.PERCCFOUTROS END,0) / 100)) )) * TGFTOP.GOLDEV ) END ) AS LUCRO, SUM( CASE WHEN TGFTOP.GOLSINAL = -1 THEN 0 ELSE ((ITE.VLRTOT - ITE.VLRDESC - CASE WHEN ITE.VLRREPRED > 0 THEN SNK_GET_ICMS_ESPECIAL_ITE(ITE.NUNOTA, ITE.SEQUENCIA, ITE.VLRREPRED, 'VLRREPRED') ELSE 0 END + ITE.VLRSUBST + ITE.VLRIPI)  * VCA.INDITENSBRUTO) * TGFTOP.GOLDEV  END ) AS VALORCOMPRA 
FROM TGFCAB


, TGFITE ITE







, TGFTOP
, VGFCAB VCA
, TGFEMP EMP
, TGFCGM CGM

, TGFPRO
, TGFGRU 
