AND TGFTOP.GOLSINAL IN (-1,1)





 


AND ITE.CODPROD = TGFPRO.CODPROD
AND CGM.CODEMP = EMP.CODEMP
AND CGM.DTREF = (SELECT MAX(C.DTREF) FROM TGFCGM C
                    WHERE C.CODEMP = EMP.CODEMP 
                      AND C.DTREF <= TGFCAB.DTNEG)


 AND EXISTS (    SELECT 1 FROM TGFCUS CUS             WHERE CUS.CODPROD = ITE.CODPROD               AND CUS.DTATUAL <= TGFCAB.DTENTSAI)  AND TGFPRO.CODGRUPOPROD = TGFGRU.CODGRUPOPROD  AND ((TGFTOP.GOLSINAL = -1 AND TGFTOP.GOLDEV = 1 AND TGFCAB.DTNEG >= ? AND TGFCAB.DTNEG <= ? ) OR (TGFTOP.GOLSINAL = -1 AND TGFTOP.GOLDEV = -1 AND TGFCAB.DTNEG >= ? AND TGFCAB.DTNEG <= ? ) OR (TGFTOP.GOLSINAL = 1 AND TGFTOP.GOLDEV = 1 AND TGFCAB.DTNEG >= ? AND TGFCAB.DTNEG <= ? ) OR (TGFTOP.GOLSINAL = 1 AND TGFTOP.GOLDEV = -1 AND TGFCAB.DTNEG >= ? AND TGFCAB.DTNEG <= ? ))  GROUP BY TGFPRO.CODGRUPOPROD, TGFGRU.DESCRGRUPOPROD , TGFTOP.GOLSINAL 